using Amazon.CDK.AWS.AppSync.Alpha;
var pluralize = Require("pluralize");

IDictionary<string, GraphqlType> args = new Dictionary<string, GraphqlType> {
    { "after", GraphqlType.String() },
    { "first", GraphqlType.Int() },
    { "before", GraphqlType.String() },
    { "last", GraphqlType.Int() }
};

InterfaceType Node = new InterfaceType("Node", new IntermediateTypeOptions {
    Definition = new Dictionary<string, IField> { { "id", GraphqlType.String() } }
});
ObjectType FilmNode = new ObjectType("FilmNode", new ObjectTypeOptions {
    InterfaceTypes = new [] { Node },
    Definition = new Dictionary<string, IField> { { "filmName", GraphqlType.String() } }
});

public IDictionary<string, ObjectType> GenerateEdgeAndConnection(ObjectType base)
{
    ObjectType edge = new ObjectType($"{base.name}Edge", new ObjectTypeOptions {
        Definition = new Dictionary<string, IField> { { "node", base.Attribute() }, { "cursor", GraphqlType.String() } }
    });
    ObjectType connection = new ObjectType($"{base.name}Connection", new ObjectTypeOptions {
        Definition = new Dictionary<string, IField> {
            { "edges", edge.Attribute(new BaseTypeOptions { IsList = true }) },
            { Pluralize(base.Name), base.Attribute(new BaseTypeOptions { IsList = true }) },
            { "totalCount", GraphqlType.Int() }
        }
    });
    return new Struct { Edge = edge, Connection = connection };
}

java.source:
import software.amazon.awscdk.services.appsync.alpha.*;
Object pluralize = require("pluralize");

Map<String, GraphqlType> args = Map.of(
        "after", GraphqlType.string(),
        "first", GraphqlType.int(),
        "before", GraphqlType.string(),
        "last", GraphqlType.int());

InterfaceType Node = InterfaceType.Builder.create("Node")
        .definition(Map.of("id", GraphqlType.string()))
        .build();
ObjectType FilmNode = ObjectType.Builder.create("FilmNode")
        .interfaceTypes(List.of(Node))
        .definition(Map.of("filmName", GraphqlType.string()))
        .build();

public Map<String, ObjectType> generateEdgeAndConnection(ObjectType base) {
    ObjectType edge = ObjectType.Builder.create(String.format("%sEdge", base.getName()))
            .definition(Map.of("node", base.attribute(), "cursor", GraphqlType.string()))
            .build();
    ObjectType connection = ObjectType.Builder.create(String.format("%sConnection", base.getName()))
            .definition(Map.of(
                    "edges", edge.attribute(BaseTypeOptions.builder().isList(true).build()),
                    pluralize(base.getName()), base.attribute(BaseTypeOptions.builder().isList(true).build()),
                    "totalCount", GraphqlType.int()))
            .build();
    return Map.of("edge", edge, "connection", connection);
}

